 p_id=>wwv_flow_api.id(91278185889245322)
,p_name=>'Two Factor Authentication'
,p_scheme_type=>'NATIVE_CUSTOM'
,p_attribute_03=>'is_authenticated'
,p_attribute_05=>'N'
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function is_authenticated (',
'  p_username in varchar2',
'  , p_password in varchar2',
') return boolean',
'is',
'  l_secret users.secret%type;',
'begin',
'  select secret',
'  into l_secret',
'  from users',
'  where lower(username) = lower(p_username)',
'    and passw = hash_password(p_password)',
'    and active = 1;',
'    ',
' -- return true;',
'    ',
'  return 1 = oos_util_totp.validate_otp(',
'    p_secret => l_secret',
'    , p_otp => :P101_OTP',
'    , p_skew => 30',
'  );',
'   ',
'exception',
'  when others then return false;',
'end;'))
,p_invalid_session_type=>'LOGIN'
,p_use_secure_cookie_yn=>'N'
,p_ras_mode=>0
);
end;
